import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Task 1: Import urlConfig
import { urlConfig } from '../../config';

// Task 2: Import useAppContext
import { useAppContext } from '../../context/AuthContext';

function RegisterPage() {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    // Task 4: Include a state for error message
    const [showerr, setShowerr] = useState('');

    // Task 5: Create local variables for navigate and setIsLoggedIn
    const navigate = useNavigate();
    const { setIsLoggedIn } = useAppContext();

    const handleRegister = async () => {
        try {
            const response = await fetch(`${urlConfig.backendUrl}/api/auth/register`, {
                // Task 6: Set method
                method: 'POST',
                // Task 7: Set headers
                headers: {
                    'Content-Type': 'application/json'
                },
                // Task 8: Set body to send user details
                body: JSON.stringify({
                    firstName,
                    lastName,
                    email,
                    password
                })
            });

            if (!response.ok) {
                throw new Error('Registration failed');
            }

            const data = await response.json();

            // Save token to sessionStorage or context
            if (data.authtoken) {
                sessionStorage.setItem('token', data.authtoken);
                sessionStorage.setItem('userEmail', data.email);
                setIsLoggedIn(true);
                navigate('/app');
            }

        } catch (e) {
            console.log("Error fetching details: " + e.message);
            setShowerr("Registration failed. Please try again.");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 col-lg-4">
                    <div className="register-card p-4 border rounded shadow-sm">
                        <h2 className="text-center mb-4 font-weight-bold">Register</h2>

                        {/* Show error message */}
                        {showerr && <div className="alert alert-danger">{showerr}</div>}

                        {/* Form fields here */}

                        <button
                            className="btn btn-primary w-100 mb-3"
                            onClick={handleRegister}
                        >
                            Register
                        </button>

                        <p className="mt-4 text-center">
                            Already a member? <a href="/app/login" className="text-primary">Login</a>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default RegisterPage;
