import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

function Navbar() {
    // Access authentication context
    const { isLoggedIn, setIsLoggedIn, userName } = useContext(AuthContext);
    const navigate = useNavigate();

    // Handle logout
    const handleLogout = () => {
        // Clear session or token (example: remove token from sessionStorage)
        sessionStorage.removeItem('token');
        setIsLoggedIn(false); // Update authentication state
        setUserName(null); // Clear user name
        navigate('/app/login'); // Redirect to login page
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
                {/* Brand/logo */}
                <Link className="navbar-brand" to="/app">
                    GiftLink
                </Link>

                {/* Toggle button for small screens */}
                <button
                    className="navbar-toggler"
                    type="button"
                    data-bs-toggle="collapse"
                    data-bs-target="#navbarNav"
                    aria-controls="navbarNav"
                    aria-expanded="false"
                    aria-label="Toggle navigation"
                >
                    <span className="navbar-toggler-icon"></span>
                </button>

                {/* Navbar links */}
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                        {/* Home link */}
                        <li className="nav-item">
                            <Link className="nav-link" to="/app">
                                Home
                            </Link>
                        </li>

                        {/* Gifts link */}
                        <li className="nav-item">
                            <Link className="nav-link" to="/app/gifts">
                                Gifts
                            </Link>
                        </li>

                        {/* Search link */}
                        <li className="nav-item">
                            <Link className="nav-link" to="/app/search">
                                Search
                            </Link>
                        </li>

                        {/* Conditional rendering based on login state */}
                        {isLoggedIn ? (
                            <>
                                {/* Logged-in user greeting */}
                                <li className="nav-item">
                                    <span className="nav-link disabled">
                                        Welcome, {userName || 'User'}!
                                    </span>
                                </li>

                                {/* Logout button */}
                                <li className="nav-item">
                                    <button
                                        className="nav-link btn btn-link"
                                        onClick={handleLogout}
                                    >
                                        Logout
                                    </button>
                                </li>
                            </>
                        ) : (
                            <>
                                {/* Login link */}
                                <li className="nav-item">
                                    <Link className="nav-link" to="/app/login">
                                        Login
                                    </Link>
                                </li>

                                {/* Register link */}
                                <li className="nav-item">
                                    <Link className="nav-link" to="/app/register">
                                        Register
                                    </Link>
                                </li>
                            </>
                        )}
                    </ul>
                </div>
            </div>
        </nav>
    );
}

export default Navbar;
