import React, { useState, useEffect } from 'react';
import { urlConfig } from '../../config'; // Import backend URL configuration
import { useAppContext } from '../../context/AuthContext'; // Import AuthContext
import { useNavigate } from 'react-router-dom'; // For navigation
import './LoginPage.css'; // Import CSS file

function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [incorrect, setIncorrect] = useState(''); // State for error messages

    const navigate = useNavigate(); // For programmatic navigation
    const bearerToken = sessionStorage.getItem('auth-token'); // Check if user is already logged in
    const { setIsLoggedIn, setUserName } = useAppContext(); // Global state management

    // Redirect if user is already logged in
    useEffect(() => {
        if (bearerToken) {
            navigate('/app');
        }
    }, [navigate, bearerToken]);

    const handleLogin = async () => {
        try {
            console.log("Inside handleLogin");
            console.log({
                email,
                password
            });

            const response = await fetch(`${urlConfig.backendUrl}/api/auth/login`, {
                method: 'POST', // Task 7: Set the POST method
                headers: {
                    'Content-Type': 'application/json', // Task 8: Set headers
                    Authorization: bearerToken ? `Bearer ${bearerToken}` : '', // Include Bearer token if available
                },
                body: JSON.stringify({ // Task 9: Set body to send user details
                    email,
                    password,
                }),
            });

            if (response.ok) {
                const data = await response.json();
                sessionStorage.setItem('auth-token', data.authtoken); // Store auth token in session storage
                sessionStorage.setItem('name', data.userName); // Store user name in session storage
                setIsLoggedIn(true); // Update global login state
                setUserName(data.userName); // Update global user name
                navigate('/app'); // Navigate to main page after successful login
            } else {
                const errorData = await response.json();
                setIncorrect(errorData.error || 'Invalid credentials'); // Display error message
            }
        } catch (e) {
            console.error('Error during login:', e.message);
            setIncorrect('An unexpected error occurred. Please try again.');
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 col-lg-4">
                    <div className="login-card p-4 border rounded shadow-sm">
                        <h2 className="text-center mb-4 font-weight-bold">Login</h2>

                        {/* Error Message */}
                        {incorrect && <p className="text-danger text-center">{incorrect}</p>}

                        <form onSubmit={(e) => e.preventDefault()}>
                            <div className="mb-3">
                                <label htmlFor="email" className="form-label">Email address</label>
                                <input
                                    id="email"
                                    type="email"
                                    className="form-control"
                                    placeholder="Enter your email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                            </div>

                            <div className="mb-3">
                                <label htmlFor="password" className="form-label">Password</label>
                                <input
                                    id="password"
                                    type="password"
                                    className="form-control"
                                    placeholder="Enter your password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                            </div>

                            <button
                                className="btn btn-primary w-100 mb-3"
                                onClick={handleLogin}
                                type="button"
                            >
                                Login
                            </button>
                        </form>

                        <p className="mt-4 text-center">
                            New here? <a href="/app/register" className="text-primary">Register Here</a>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default LoginPage;
